<style>
    
    

    header {
        display: flex;
        height: 60rem;
        flex-direction: column;
        background-image: url('src/img/homemCelullar.jpg');
        background-size: cover;


    }

   

    .textoInicial {
        font-family: "Raleway", sans-serif;
        font-optical-sizing: auto;
        font-weight: 200;
        font-style: normal;
        color: white;
        font-size: 8rem;
        width: 45%;
        height: 80%;
        display: flex;
        justify-content: center;
        flex-direction: column;
    }

    .textoInicialDentro {
        color: white;
        height: 70%;
        width: 95%;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        font-family: didot regular;

    }

    .botaoTextoInicial {
        width: 100%;
        display: flex;


    }

    .botaoTextoInicial>input {
        font-size: 2rem;
        align-items: center;
        padding: 5px 25px;
        border-radius: 12px;
        background-color: #28a3c2;
        border: none;
        color: #DDEAF6;
        font-family: "Raleway", sans-serif;
        font-optical-sizing: auto;
        font-weight: 200;
        font-style: normal;
    }

    .div1 {
        width: 100%;
        height: 60rem;
        padding: 0% 2%;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .div1Dentro {
        width: 90%;
        height: 80%;
        display: flex;
        align-items: center;
        flex-direction: column;
    }
    .textoDiv1{
        display: flex;
        align-items: center;
        flex-direction: column;
    }
    .textoDiv1>h1{
       font-size: 5rem;
    }
    .listra{
        width: 45%;
        height: 4px;
        background-color: #1ae0ef;
        margin-top: 5px;
    }

    .div1Bloco {
        height: 60rem;
        width: 80rem;
        display: flex;
        justify-content: center;
        align-items: center;
        gap: 5%;
        margin-top: 15px;
    }

    .blocos1 {
        font-family: "Raleway", sans-serif;
        font-optical-sizing: auto;
        font-weight: 200;
        width: 19rem;
        height: 28rem;
        border-radius: 5px;
        background-color: whitesmoke;
        box-shadow:
                0 2.8px 2.2px rgba(0, 0, 0, 0.034),
                0 6.7px 5.3px rgba(0, 0, 0, 0.048),
                0 12.5px 10px rgba(0, 0, 0, 0.06),
                0 22.3px 17.9px rgba(0, 0, 0, 0.072),
                0 10.8px 10.4px rgba(0, 0, 0, 0.086),
                0 100px 80px rgba(0, 0, 0, 0.12);


    }

    .imgBloco1 {
        width: 100%;
        height: 43%;
        border-top-left-radius: 5px;
        border-top-right-radius: 5px;
        
    }

    .imgBloco1>img {
        width: 100%;
        height: 100%;
        border-top-left-radius: 5px;
        border-top-right-radius: 5px;

    }

    .textoBloco1{
        height: 50%;
        align-items: center;
        justify-content: center;
        font-size: 15px;
        padding: 20px;
        text-align: justify;    
    }

    .div2 {
        width: 100%;
        height: 60rem;
        padding: 0% 2%;
        display: flex;
        align-items: center;
        justify-content: center;
    }


   




    @media screen and (max-width: 1333px) {


        .textoInicial {
            font-size: 6rem;
            width: 50%;
        }

        .botaoTextoInicial>input {
            padding: 3px 15px;
        }
        a,
        i{
            color: white;
        }

    }
</style>

